  constructor(){
    super();
    this.state={
      turn:"X",
      gameEnded:false,
      winner:undefined,
      board:Array(9).fill(''),
      totalMoves: 0
    }

  }
  clicked(e){
    if(this.state.board[e.target.dataset.square]==''){
    this.state.board[e.target.dataset.square]=this.state.turn;

    console.log(this.state.board);
    e.target.innerText=this.state.turn;
    this.setState({turn:this.state.turn=='X'?'O':'X',
  board:this.state.board,
  totalMoves:this.state.totalMoves++
  })
}
var result= this.checkwinner();
if(result=='X'){
this.setState({
  gameEnded :  true,
  winner:'X'
}
);
console.log('this.state.gameEnded');
}

else if(result=='O'){
  this.setState({
    gameEnded :  true,
    winner:'Y'
  }
  );
  console.log('this.state.gameEnded');
}


  }

  checkwinner(){
    var moves=[[0,3,5], [1,4,7], [2,5,8], [0,4,8], [2,4,6], [0,1,2],[3,4,5],[6,7,8]];
    var board=this.state.board;
    for (let i=0;i<moves.length;i++){
      if(board[moves[i][0]]==board[moves[i][1]]&&board[moves[i][1]]==board[moves[i][2]]){
